mapper.insert:
insert into cn_group (group_id, group_name, school_id
  )
values (#{groupId,jdbcType=BIGINT}, #{groupName,jdbcType=VARCHAR}, #{schoolId,jdbcType=BIGINT}
  )

mapper.update:
update cn_group
set group_name = #{groupName,jdbcType=VARCHAR},
  school_id = #{schoolId,jdbcType=BIGINT}
where group_id = #{groupId,jdbcType=BIGINT}

mapper.select:
select group_id, group_name, school_id
from cn_group
where group_id = #{groupId,jdbcType=BIGINT}
